package com.gasmonitor.config;import org.springframework.context.annotation.Configuration;import org.springframework.security.config.annotation.web.messaging.MessageSecurityMetadataSourceRegistry;/** * Created by saplmm on 2017/7/10. */@Configurationpublic class WebSocketSecurityConfig {    protected void configureInbound(MessageSecurityMetadataSourceRegistry messages) {        messages.simpDestMatchers("/user/*").authenticated();    }}