package com.gasmonitor.controller.sys;import com.gasmonitor.service.bas.DataInitService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;/** * Created by saplmm on 2017/9/1. */@Controller@RequestMapping("datainit")public class DataInitController {    @Autowired    private DataInitService dataInitService;    /**     * 通过以前的数据 初始化租户的信息     *     * @return     */    @RequestMapping("tenant/tenantuser")    @ResponseBody    public Object AjaxInitTenantUser() {        return dataInitService.initTenantUser();    }    /**     * 初始化站点的信息     *     * @return     */    @RequestMapping("site/siteinit")    @ResponseBody    public Object AjaxInitSite() {        return null;    }}