package com.gasmonitor.dao;import com.gasmonitor.entity.DeviceWarnEvent;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import java.util.List;/** * Created by saplmm on 2017/8/28. */public interface DeviceWarnEventRepository extends JpaRepository<DeviceWarnEvent, Long> {    List<DeviceWarnEvent> findByTenantId(Long tenantId);    @Query("update DeviceWarnEvent set status := status where id := warnId")    @Modifying    int updateStatus(Integer status, Long warnId);}