package com.gasmonitor.entity;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import java.util.Date;/** * Created by saplmm on 2017/7/28. */@Entitypublic class BasDeviceUnit {    @Id    @GeneratedValue    private Long id;    private String unint;    private Date updatedate;    public Date getUpdatedate() {        return updatedate;    }    public void setUpdatedate(Date updatedate) {        this.updatedate = updatedate;    }    public String getUnint() {        return unint;    }    public void setUnint(String unint) {        this.unint = unint;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    @Override    public String toString() {        return "BasDeviceUnit{" +                "id=" + id +                ", unint='" + unint + '\'' +                ", updatedate=" + updatedate +                '}';    }}