package com.gasmonitor.entity;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;/** * Created by saplmm on 2017/8/18. */@Entitypublic class BasRole {    @Id    @GeneratedValue    private Long id;    private String role;    private String roleDes;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getRole() {        return role;    }    public void setRole(String role) {        this.role = role;    }    public String getRoleDes() {        return roleDes;    }    public void setRoleDes(String roleDes) {        this.roleDes = roleDes;    }    @Override    public String toString() {        return "BasRole{" +                "id=" + id +                ", role='" + role + '\'' +                ", roleDes='" + roleDes + '\'' +                '}';    }}