package com.gasmonitor.entity;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import java.util.Date;/////**// * Created by saplmm on 2017/6/18.// */////@Entitypublic class Device {    @Id    @GeneratedValue    private String id;  //设备Id    private String tokenId;    private String name;    //设备名称    private Integer logic;    private String watcher;    private String phone;    private Date created;    private Integer status;    private String parent;    private Integer siteId;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getTokenId() {        return tokenId;    }    public void setTokenId(String tokenId) {        this.tokenId = tokenId;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getLogic() {        return logic;    }    public void setLogic(Integer logic) {        this.logic = logic;    }    public String getWathcer() {        return watcher;    }    public void setWathcer(String watcher) {        this.watcher = watcher;    }    public String getPhone() {        return phone;    }    public void setPhone(String phone) {        this.phone = phone;    }    public Date getCreated() {        return created;    }    public void setCreated(Date created) {        this.created = created;    }    public Integer getStatus() {        return status;    }    public void setStatus(Integer status) {        this.status = status;    }    public String getParent() {        return parent;    }    public void setParent(String parent) {        this.parent = parent;    }    public Integer getSiteId() {        return siteId;    }    public void setSiteId(Integer siteId) {        this.siteId = siteId;    }}