package com.gasmonitor.entity;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;/** * Created by saplmm on 2017/6/8. * 租户 */@Entitypublic class Tenant {    @Id    @GeneratedValue    private Long id;    private String name;    private String mobile;    private String password;    public Tenant() {    }    @Override    public String toString() {        return "Tenant{" +                "id=" + id +                ", name='" + name + '\'' +                ", mobile='" + mobile + '\'' +                ", password='" + password + '\'' +                '}';    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getMobile() {        return mobile;    }    public void setMobile(String mobile) {        this.mobile = mobile;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }}