package com.gasmonitor.exception;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.servlet.ModelAndView;/** * Created by saplmm on 2017/7/25. */@ControllerAdvicepublic class GlobalDefaultExceptionHandler {    @ExceptionHandler(value = TipsException.class)    public ModelAndView defaultErrorHandler(Exception e) throws Exception {        ModelAndView mv = new ModelAndView();        mv.setViewName("exception/tipsexception");        mv.addObject("msg", e.getMessage());        return mv;    }}