package com.gasmonitor.pros;import org.springframework.boot.context.properties.ConfigurationProperties;/** * Created by saplmm on 2017/7/6. */@ConfigurationProperties(prefix = "hazelcast")public class HazelCastPros {    private String name;    private String nametopic;    private String nameinstance;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getNametopic() {        return nametopic;    }    public void setNametopic(String nametopic) {        this.nametopic = nametopic;    }    public String getNameinstance() {        return nameinstance;    }    public void setNameinstance(String nameinstance) {        this.nameinstance = nameinstance;    }}