package com.gasmonitor.service.security;import org.springframework.security.authentication.AuthenticationDetailsSource;import org.springframework.security.web.authentication.WebAuthenticationDetails;import org.springframework.stereotype.Component;import javax.servlet.http.HttpServletRequest;/** * Created by saplmm on 2017/7/22. */@Componentpublic class CustomAuthenticationDetailsSource implements AuthenticationDetailsSource<HttpServletRequest, WebAuthenticationDetails> {    @Override    public WebAuthenticationDetails buildDetails(HttpServletRequest context) {        return new CustomWebAuthenticationDetails(context);    }}