package com.gasmonitor.service.security;import org.springframework.security.web.authentication.WebAuthenticationDetails;import javax.servlet.http.HttpServletRequest;/** * Created by saplmm on 2017/7/22. */public class CustomWebAuthenticationDetails extends WebAuthenticationDetails {    /**     *     */    private static final long serialVersionUID = 6975601077710753878L;    private final String token;    private final String deviceId;    public CustomWebAuthenticationDetails(HttpServletRequest request) {        super(request);        token = request.getParameter("token");        this.deviceId = request.getParameter("deviceId");    }    public String getToken() {        return token;    }    public String getDeviceId() {        return deviceId;    }    @Override    public String toString() {        return "CustomWebAuthenticationDetails{" +                "token='" + token + '\'' +                ", deviceId='" + deviceId + '\'' +                '}';    }}