package com.gasmonitor.utils;import com.gasmonitor.entity.Tenant;import javax.servlet.http.HttpSession;/** * Created by saplmm on 2017/7/15. */public class SessionUtils {    public static Tenant getTanat(HttpSession session) {        Object ret = session.getAttribute("tenant");        if (ret != null) {            return (Tenant) ret;        }        return null;    }}