package com.gasmonitor.vo;import java.util.List;/** * Created by saplmm on 2017/6/23. */public class AjaxResult<T> {    private int total;    private int page;    private List<T> data;    public static <M> AjaxResult<M> NewAjaxResult(List<M> t, int total, int page) {        AjaxResult<M> r = new AjaxResult<M>();        r.setData(t);        r.setTotal(total);        r.setPage(page);        return r;    }    @Override    public String toString() {        return "AjaxResult{" +                "total=" + total +                ", page=" + page +                ", data=" + data +                '}';    }    public int getTotal() {        return total;    }    public void setTotal(int total) {        this.total = total;    }    public int getPage() {        return page;    }    public void setPage(int page) {        this.page = page;    }    public List<T> getData() {        return data;    }    public void setData(List<T> data) {        this.data = data;    }}